import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const DonationHistory = () => {
  const [donations, setDonations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [totalDonations, setTotalDonations] = useState({ totalAmount: 0, totalDonations: 0 });
  const navigate = useNavigate();

  const fetchDonations = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const [donationsResponse, totalsResponse] = await Promise.all([
        axios.get('http://localhost:5000/api/donations/my-donations', {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get('http://localhost:5000/api/donations/my-total', {
          headers: { Authorization: `Bearer ${token}` }
        })
      ]);

      setDonations(donationsResponse.data);
      setTotalDonations(totalsResponse.data);
      setError('');
    } catch (error) {
      console.error('Error fetching donations:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setError('Failed to fetch donations. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    fetchDonations();
  }, [fetchDonations]);

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={fetchDonations}
            className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-center text-gray-900 mb-8">My Donation History</h1>

        {/* Total Donations Card */}
        <div className="bg-white rounded-2xl shadow-xl p-6 mb-8 transform transition-all duration-300 hover:scale-105">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-purple-50 rounded-xl p-6 text-center">
              <h3 className="text-lg font-semibold text-gray-700 mb-2">Total Donations</h3>
              <p className="text-3xl font-bold text-purple-600">{totalDonations.totalDonations}</p>
            </div>
            <div className="bg-pink-50 rounded-xl p-6 text-center">
              <h3 className="text-lg font-semibold text-gray-700 mb-2">Total Amount</h3>
              <p className="text-3xl font-bold text-pink-600">₹{totalDonations.totalAmount.toLocaleString()}</p>
            </div>
          </div>
        </div>

        {/* Donations List */}
        <div className="space-y-6">
          {donations.map((donation) => (
            <div
              key={donation._id}
              className="bg-white rounded-2xl shadow-lg p-6 transform transition-all duration-300 hover:scale-105"
            >
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="text-xl font-semibold text-gray-900">{donation.charityName}</h3>
                  <p className="text-gray-600 mt-1">
                    Donated on {new Date(donation.date).toLocaleDateString()}
                  </p>
                </div>
                <div className="text-right">
                  <p className="text-2xl font-bold text-purple-600">₹{donation.amount}</p>
                </div>
              </div>

              <div className="mt-4 grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-gray-500">Category</p>
                  <p className="font-medium text-gray-700">{donation.category}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-500">Payment Mode</p>
                  <p className="font-medium text-gray-700">{donation.paymentMode}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {donations.length === 0 && (
          <div className="text-center py-12">
            <p className="text-gray-600">No donations found. Start making a difference today!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default DonationHistory;
