import React, { useState } from 'react';
import axios from 'axios';
import UPIPayment from './UPIPayment';
import CreditCardPayment from './CreditCardPayment';

const DonationForm = () => {
  const [formData, setFormData] = useState({
    amount: '',
    charityName: '',
    donorName: '',
    address: '',
    pinCode: '',
    paymentMethod: 'card'
  });
  const [step, setStep] = useState(1); // 1 for donor details, 2 for payment
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const validateForm = () => {
    const amountNum = parseFloat(formData.amount);
    if (isNaN(amountNum) || amountNum <= 0) {
      setError('Please enter a valid amount greater than 0');
      return false;
    }

    if (!formData.charityName.trim()) {
      setError('Please enter a charity name');
      return false;
    }

    if (!formData.donorName.trim()) {
      setError('Please enter your name');
      return false;
    }

    if (!formData.address.trim()) {
      setError('Please enter your address');
      return false;
    }

    if (!/^\d{6}$/.test(formData.pinCode)) {
      setError('Please enter a valid 6-digit PIN code');
      return false;
    }

    setError('');
    return true;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) setError('');
  };

  const submitDonation = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please login to make a donation');
        return;
      }

      await axios.post(
        'http://localhost:5000/api/donations',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setSuccess('Donation submitted successfully!');
      setFormData({
        amount: '',
        charityName: '',
        donorName: '',
        address: '',
        pinCode: '',
        paymentMethod: 'card'
      });
      setStep(1); // Reset to first step
    } catch (err) {
      console.error('Error submitting donation:', err);
      if (err.response?.status === 401) {
        setError('Your session has expired. Please login again.');
        localStorage.removeItem('token');
      } else if (err.response?.status === 400) {
        setError(err.response.data.message || 'Invalid donation data');
      } else {
        setError('Failed to submit donation. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleNextStep = (e) => {
    e.preventDefault();
    if (validateForm()) {
      setStep(2);
    }
  };

  const handlePaymentSuccess = () => {
    setLoading(true);
    submitDonation();
  };

  const renderDonorDetails = () => (
    <div className="space-y-6">
      <div>
        <label htmlFor="amount" className="block text-sm font-medium text-gray-700 mb-1">
          Amount (₹)
        </label>
        <input
          type="number"
          id="amount"
          name="amount"
          value={formData.amount}
          onChange={handleChange}
          required
          min="1"
          step="0.01"
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
          placeholder="Enter amount"
        />
      </div>

      <div>
        <label htmlFor="charityName" className="block text-sm font-medium text-gray-700 mb-1">
          Charity Name
        </label>
        <input
          type="text"
          id="charityName"
          name="charityName"
          value={formData.charityName}
          onChange={handleChange}
          required
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
          placeholder="Enter charity name"
        />
      </div>

      <div>
        <label htmlFor="donorName" className="block text-sm font-medium text-gray-700 mb-1">
          Your Name
        </label>
        <input
          type="text"
          id="donorName"
          name="donorName"
          value={formData.donorName}
          onChange={handleChange}
          required
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
          placeholder="Enter your name"
        />
      </div>

      <div>
        <label htmlFor="address" className="block text-sm font-medium text-gray-700 mb-1">
          Address
        </label>
        <textarea
          id="address"
          name="address"
          value={formData.address}
          onChange={handleChange}
          required
          rows="3"
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
          placeholder="Enter your address"
        />
      </div>

      <div>
        <label htmlFor="pinCode" className="block text-sm font-medium text-gray-700 mb-1">
          PIN Code
        </label>
        <input
          type="text"
          id="pinCode"
          name="pinCode"
          value={formData.pinCode}
          onChange={handleChange}
          required
          pattern="[0-9]{6}"
          maxLength="6"
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
          placeholder="Enter 6-digit PIN code"
        />
      </div>

      <button
        type="button"
        onClick={handleNextStep}
        className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-3 px-6 rounded-lg transition duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
      >
        Continue to Payment
      </button>
    </div>
  );

  const renderPaymentOptions = () => (
    <div className="space-y-6">
      <div className="bg-gray-50 p-4 rounded-lg">
        <h3 className="text-lg font-semibold text-gray-800 mb-2">Donation Summary</h3>
        <div className="space-y-2">
          <p className="text-gray-600">Amount: ₹{formData.amount}</p>
          <p className="text-gray-600">Charity: {formData.charityName}</p>
          <p className="text-gray-600">Donor: {formData.donorName}</p>
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-3">
          Select Payment Method
        </label>
        <div className="space-y-2">
          <label className="flex items-center space-x-2">
            <input
              type="radio"
              name="paymentMethod"
              value="card"
              checked={formData.paymentMethod === 'card'}
              onChange={handleChange}
              className="h-4 w-4 text-purple-600"
            />
            <span>Credit/Debit Card</span>
          </label>
          <label className="flex items-center space-x-2">
            <input
              type="radio"
              name="paymentMethod"
              value="upi"
              checked={formData.paymentMethod === 'upi'}
              onChange={handleChange}
              className="h-4 w-4 text-purple-600"
            />
            <span>UPI Payment</span>
          </label>
        </div>
      </div>

      {formData.paymentMethod === 'upi' ? (
        <UPIPayment
          amount={formData.amount}
          onSuccess={handlePaymentSuccess}
          disabled={loading}
        />
      ) : (
        <CreditCardPayment
          amount={formData.amount}
          onSuccess={handlePaymentSuccess}
          disabled={loading}
        />
      )}

      <button
        type="button"
        onClick={() => setStep(1)}
        className="w-full bg-gray-100 hover:bg-gray-200 text-gray-800 font-semibold py-3 px-6 rounded-lg transition duration-300"
      >
        Back to Details
      </button>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Make a Difference</h1>
          <p className="text-xl text-gray-600">Your contribution helps us create a better world</p>
        </div>

        <div className="bg-white p-8 rounded-2xl shadow-xl">
          <div className="flex justify-center mb-8">
            <div className="flex items-center">
              <div className={`w-8 h-8 rounded-full flex items-center justify-center ${step === 1 ? 'bg-purple-600 text-white' : 'bg-gray-200 text-gray-600'}`}>
                1
              </div>
              <div className="w-24 h-1 bg-gray-200 mx-2">
                <div className={`h-full ${step === 2 ? 'bg-purple-600' : 'bg-gray-200'}`}></div>
              </div>
              <div className={`w-8 h-8 rounded-full flex items-center justify-center ${step === 2 ? 'bg-purple-600 text-white' : 'bg-gray-200 text-gray-600'}`}>
                2
              </div>
            </div>
          </div>

          <h2 className="text-2xl font-bold text-gray-800 mb-6">
            {step === 1 ? 'Donor Details' : 'Payment Options'}
          </h2>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
              {error}
            </div>
          )}

          {success && (
            <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
              {success}
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            {step === 1 ? renderDonorDetails() : renderPaymentOptions()}
          </form>
        </div>
      </div>
    </div>
  );
};

export default DonationForm;
