import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const donationCategories = [
  { id: 'food', name: 'Food Donation', emoji: 'ðŸ±' },
  { id: 'clothing', name: 'Clothing Donation', emoji: 'ðŸ‘•' },
  { id: 'cancer', name: 'Cancer Patient Support', emoji: 'ðŸŽ—ï¸' },
  { id: 'shelter', name: 'Shelter Support', emoji: 'ðŸ ' },
  { id: 'education', name: 'Education Aid', emoji: 'ðŸ“š' },
  { id: 'medical', name: 'Medical Supplies', emoji: 'ðŸ’Š' },
  { id: 'hygiene', name: 'Hygiene Kits', emoji: 'ðŸ§¼' },
  { id: 'festival', name: 'Festival Hampers', emoji: 'ðŸŽ' },
  { id: 'technology', name: 'Technology for Students', emoji: 'ðŸ’»' },
  { id: 'childcare', name: 'Child Care Essentials', emoji: 'ðŸ¼' }
];

const DonationHistory = () => {
  const [donations, setDonations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [totalDonations, setTotalDonations] = useState(0);
  const navigate = useNavigate();

  const fetchDonations = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const response = await axios.get('http://localhost:5000/api/donations', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.status === 200) {
        setDonations(response.data.donations);
        // Calculate total donations
        const total = response.data.donations.reduce((sum, donation) => sum + Number(donation.amount), 0);
        setTotalDonations(total);
      }
    } catch (error) {
      console.error('Error fetching donations:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setError('Failed to fetch donations. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    fetchDonations();
  }, [fetchDonations]);

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={fetchDonations}
            className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden mb-8">
          <div className="p-6 sm:p-8">
            <h2 className="text-3xl font-bold text-gray-900 mb-6">My Donations</h2>

            {/* Total Donations Card */}
            <div className="bg-gradient-to-r from-purple-600 to-pink-600 rounded-xl p-6 mb-8 text-white transform transition-all duration-300 hover:scale-105 hover:shadow-xl">
              <h3 className="text-lg font-semibold mb-2">Total Donations</h3>
              <p className="text-4xl font-bold">â‚¹{totalDonations.toLocaleString()}</p>
              <p className="text-sm opacity-90 mt-2">Thank you for your generosity!</p>
            </div>

            {/* Donations List */}
            <div className="space-y-6">
              {donations.length === 0 ? (
                <div className="text-center py-8">
                  <p className="text-gray-600">No donations found. Make your first donation today!</p>
                </div>
              ) : (
                donations.map((donation) => (
                  <div
                    key={donation._id}
                    className="bg-gray-50 rounded-xl p-6 transform transition-all duration-300 hover:scale-105 hover:shadow-lg"
                  >
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="text-xl font-semibold text-gray-900">{donation.charityName}</h3>
                        <p className="text-gray-600 mt-1">
                          {new Date(donation.createdAt).toLocaleDateString('en-US', {
                            year: 'numeric',
                            month: 'long',
                            day: 'numeric'
                          })}
                        </p>
                      </div>
                      <div className="text-right">
                        <p className="text-2xl font-bold text-purple-600">â‚¹{donation.amount.toLocaleString()}</p>
                        <p className="text-sm text-gray-500 mt-1">Paid via {donation.paymentMode}</p>
                      </div>
                    </div>
                    <div className="mt-4 flex items-center space-x-2">
                      <span className="text-2xl">
                        {donationCategories.find(cat => cat.id === donation.category)?.emoji}
                      </span>
                      <span className="text-gray-700">
                        {donationCategories.find(cat => cat.id === donation.category)?.name}
                      </span>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DonationHistory;
