import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import DonationForm from './components/DonationForm';
import DonationHistory from './components/DonationHistory';
import UserProfile from './components/UserProfile';
import AdminDashboard from './components/AdminDashboard';

const App = () => {
  const isAuthenticated = !!localStorage.getItem('token');
  const isAdmin = localStorage.getItem('isAdmin') === 'true';

  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50">
        <Navbar />
        <main className="container mx-auto px-4 py-8">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={!isAuthenticated ? <Login /> : <Navigate to="/profile" />} />
            <Route path="/register" element={!isAuthenticated ? <Register /> : <Navigate to="/profile" />} />
            <Route path="/donate" element={isAuthenticated ? <DonationForm /> : <Navigate to="/login" />} />
            <Route path="/my-donations" element={isAuthenticated ? <DonationHistory /> : <Navigate to="/login" />} />
            <Route path="/profile" element={isAuthenticated ? <UserProfile /> : <Navigate to="/login" />} />
            <Route
              path="/admin/dashboard"
              element={isAuthenticated && isAdmin ? <AdminDashboard /> : <Navigate to="/" />}
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
};

export default App;
