import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import ThankYouMessage from './ThankYouMessage';
import PaymentOptions from './PaymentOptions';

const donationCategories = [
  { id: 'food', name: 'Food Donation', emoji: 'ðŸ²' },
  { id: 'clothing', name: 'Clothing Donation', emoji: 'ðŸ‘•' },
  { id: 'cancer', name: 'Cancer Patient Support', emoji: 'â¤ï¸' },
  { id: 'shelter', name: 'Shelter Support', emoji: 'ðŸ ' },
  { id: 'education', name: 'Education Aid', emoji: 'ðŸ“š' },
  { id: 'medical', name: 'Medical Supplies', emoji: 'ðŸ’Š' },
  { id: 'hygiene', name: 'Hygiene Kits', emoji: 'ðŸ§¼' },
  { id: 'festival', name: 'Festival Hampers', emoji: 'ðŸŽ' },
  { id: 'technology', name: 'Technology for Students', emoji: 'ðŸ’»' },
  { id: 'childcare', name: 'Child Care Essentials', emoji: 'ðŸ‘¶' }
];

const paymentModes = [
  { id: 'upi', name: 'UPI', emoji: 'ðŸ’³' },
  { id: 'card', name: 'Credit/Debit Card', emoji: 'ðŸ’³' },
  { id: 'netbanking', name: 'Net Banking', emoji: 'ðŸ¦' },
  { id: 'wallet', name: 'Digital Wallet', emoji: 'ðŸ’°' }
];

const DonationForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    amount: '',
    charityName: '',
    donorName: '',
    address: '',
    pinCode: '',
    category: '',
    paymentMode: '',
    message: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [showThankYou, setShowThankYou] = useState(false);
  const [showPaymentOptions, setShowPaymentOptions] = useState(false);
  const [donationDetails, setDonationDetails] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.amount || isNaN(formData.amount) || formData.amount <= 0) {
      newErrors.amount = 'Please enter a valid amount';
    }
    if (!formData.charityName.trim()) {
      newErrors.charityName = 'Please enter charity name';
    }
    if (!formData.donorName.trim()) {
      newErrors.donorName = 'Please enter your name';
    }
    if (!formData.address.trim()) {
      newErrors.address = 'Please enter your address';
    }
    if (!formData.pinCode || !/^\d{6}$/.test(formData.pinCode)) {
      newErrors.pinCode = 'Please enter a valid 6-digit PIN code';
    }
    if (!formData.category) {
      newErrors.category = 'Please select a donation category';
    }
    if (!formData.paymentMode) {
      newErrors.paymentMode = 'Please select a payment mode';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handlePaymentSelect = (paymentMode) => {
    setFormData({ ...formData, paymentMode });
    handleSubmit(paymentMode);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const response = await axios.post(
        'http://localhost:5000/api/donations',
        formData,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data) {
        // Store donation details before resetting form
        setDonationDetails({
          amount: formData.amount,
          charityName: formData.charityName,
          category: formData.category
        });

        // Reset form
        setFormData({
          amount: '',
          charityName: '',
          donorName: '',
          address: '',
          pinCode: '',
          category: '',
          paymentMode: '',
          message: ''
        });

        setShowThankYou(true);
      }
    } catch (error) {
      console.error('Donation error:', error);
      setErrors({
        submit: error.response?.data?.message || 'Failed to process donation. Please try again.'
      });
    } finally {
      setLoading(false);
    }
  };

  if (showThankYou && donationDetails) {
    return (
      <ThankYouMessage
        amount={donationDetails.amount}
        charityName={donationDetails.charityName}
        category={donationDetails.category}
      />
    );
  }

  if (showPaymentOptions) {
    return (
      <PaymentOptions
        onPaymentSelect={handlePaymentSelect}
        onCancel={() => setShowPaymentOptions(false)}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl mx-auto">
        <div className="bg-white rounded-2xl shadow-lg p-8">
          <h2 className="text-3xl font-bold text-center text-gray-900 mb-8">Make a Donation</h2>

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Amount */}
            <div>
              <label htmlFor="amount" className="block text-sm font-medium text-gray-700 mb-1">
                Donation Amount (â‚¹)
              </label>
              <input
                type="number"
                id="amount"
                name="amount"
                value={formData.amount}
                onChange={handleChange}
                className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 ${
                  errors.amount ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Enter amount"
                min="1"
              />
              {errors.amount && <p className="mt-1 text-sm text-red-600">{errors.amount}</p>}
            </div>

            {/* Charity Name */}
            <div>
              <label htmlFor="charityName" className="block text-sm font-medium text-gray-700 mb-1">
                Charity Name
              </label>
              <input
                type="text"
                id="charityName"
                name="charityName"
                value={formData.charityName}
                onChange={handleChange}
                className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 ${
                  errors.charityName ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Enter charity name"
              />
              {errors.charityName && <p className="mt-1 text-sm text-red-600">{errors.charityName}</p>}
            </div>

            {/* Donation Category */}
            <div>
              <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">
                Donation Category
              </label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 ${
                  errors.category ? 'border-red-500' : 'border-gray-300'
                }`}
              >
                <option value="">Select a category</option>
                {donationCategories.map((category) => (
                  <option key={category.id} value={category.id}>
                    {category.emoji} {category.name}
                  </option>
                ))}
              </select>
              {errors.category && <p className="mt-1 text-sm text-red-600">{errors.category}</p>}
            </div>

            {/* Payment Mode */}
            <div>
              <label htmlFor="paymentMode" className="block text-sm font-medium text-gray-700 mb-1">
                Payment Mode
              </label>
              <select
                id="paymentMode"
                name="paymentMode"
                value={formData.paymentMode}
                onChange={handleChange}
                className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 ${
                  errors.paymentMode ? 'border-red-500' : 'border-gray-300'
                }`}
              >
                <option value="">Select payment mode</option>
                {paymentModes.map((mode) => (
                  <option key={mode.id} value={mode.id}>
                    {mode.emoji} {mode.name}
                  </option>
                ))}
              </select>
              {errors.paymentMode && <p className="mt-1 text-sm text-red-600">{errors.paymentMode}</p>}
            </div>

            {/* Donor Name */}
            <div>
              <label htmlFor="donorName" className="block text-sm font-medium text-gray-700 mb-1">
                Your Name
              </label>
              <input
                type="text"
                id="donorName"
                name="donorName"
                value={formData.donorName}
                onChange={handleChange}
                className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 ${
                  errors.donorName ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Enter your name"
              />
              {errors.donorName && <p className="mt-1 text-sm text-red-600">{errors.donorName}</p>}
            </div>

            {/* Address */}
            <div>
              <label htmlFor="address" className="block text-sm font-medium text-gray-700 mb-1">
                Your Address
              </label>
              <textarea
                id="address"
                name="address"
                value={formData.address}
                onChange={handleChange}
                rows="3"
                className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 ${
                  errors.address ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Enter your address"
              />
              {errors.address && <p className="mt-1 text-sm text-red-600">{errors.address}</p>}
            </div>

            {/* PIN Code */}
            <div>
              <label htmlFor="pinCode" className="block text-sm font-medium text-gray-700 mb-1">
                PIN Code
              </label>
              <input
                type="text"
                id="pinCode"
                name="pinCode"
                value={formData.pinCode}
                onChange={handleChange}
                className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 ${
                  errors.pinCode ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Enter 6-digit PIN code"
                maxLength="6"
              />
              {errors.pinCode && <p className="mt-1 text-sm text-red-600">{errors.pinCode}</p>}
            </div>

            {/* Message */}
            <div>
              <label htmlFor="message" className="block text-sm font-medium text-gray-700 mb-1">
                Message (Optional)
              </label>
              <textarea
                id="message"
                name="message"
                value={formData.message}
                onChange={handleChange}
                rows="3"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
                placeholder="Add a message with your donation"
              />
            </div>

            {errors.submit && (
              <div className="text-red-600 text-sm text-center">{errors.submit}</div>
            )}

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-purple-600 text-white py-3 px-6 rounded-lg hover:bg-purple-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? (
                <div className="flex items-center justify-center">
                  <div className="animate-spin rounded-full h-5 w-5 border-t-2 border-b-2 border-white mr-2"></div>
                  Processing...
                </div>
              ) : (
                'Donate Now'
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DonationForm;
