import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import DonationForm from '../components/DonationForm';
import DonationHistory from '../components/DonationHistory';

const Home = () => {
  const { user } = useContext(AuthContext);
  const [donations, setDonations] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate('/login');
    } else {
      fetchDonations();
    }
  }, [user, navigate]);

  const fetchDonations = async () => {
    try {
      const res = await fetch('/api/donations', {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      });
      const data = await res.json();
      setDonations(data);
    } catch (error) {
      console.error('Error fetching donations:', error);
    }
  };

  const handleDonate = async (donationData) => {
    try {
      const res = await fetch('/api/donations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify(donationData),
      });

      if (res.ok) {
        fetchDonations(); // Refresh donations after successful donation
      } else {
        console.error('Error creating donation');
      }
    } catch (error) {
      console.error('Error creating donation:', error);
    }
  };

  if (!user) {
    return <p>Redirecting to login...</p>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Welcome, {user.name}!</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">Make a Donation</h2>
          <DonationForm onDonate={handleDonate} />
        </div>
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">Your Donation History</h2>
          <DonationHistory donations={donations} />
        </div>
      </div>
    </div>
  );
};

export default Home;
