import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DonationHistory = () => {
  const [donations, setDonations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [totalDonations, setTotalDonations] = useState(0);
  const navigate = useNavigate();

  const fetchDonations = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please login to view your donations');
        setLoading(false);
        navigate('/login');
        return;
      }

      const [donationsResponse, totalResponse] = await Promise.all([
        axios.get('http://localhost:5000/api/donations/my-donations', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }),
        axios.get('http://localhost:5000/api/donations/total', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        })
      ]);

      setDonations(donationsResponse.data);
      setTotalDonations(totalResponse.data.total);
      setError('');
    } catch (err) {
      console.error('Error fetching donations:', err);
      if (err.response?.status === 401) {
        if (err.response?.data?.message === 'Token expired') {
          setError('Your session has expired. Please login again.');
        } else if (err.response?.data?.message === 'Invalid token') {
          setError('Invalid session. Please login again.');
        } else {
          setError('Please login to view your donations');
        }
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setError('Failed to fetch donations. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    fetchDonations();
  }, [fetchDonations]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-4xl mx-auto">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading your donations...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Your Donation History</h1>
          <p className="text-xl text-gray-600">Track your contributions and impact</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div className="bg-white p-8 rounded-2xl shadow-xl">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">Total Donations</h2>
            <div className="text-center">
              <div className="text-5xl font-bold text-purple-600 mb-4">
                ₹{totalDonations.toLocaleString()}
              </div>
              <p className="text-gray-600">Thank you for your generous contributions!</p>
            </div>
            
            <div className="mt-8">
              <h3 className="text-xl font-semibold text-gray-800 mb-4">How Your Donation Helps</h3>
              <ul className="space-y-3">
                <li className="flex items-center space-x-2">
                  <span className="text-purple-500">✓</span>
                  <span>Support essential services</span>
                </li>
                <li className="flex items-center space-x-2">
                  <span className="text-purple-500">✓</span>
                  <span>Fund community programs</span>
                </li>
                <li className="flex items-center space-x-2">
                  <span className="text-purple-500">✓</span>
                  <span>Make a lasting impact</span>
                </li>
              </ul>
            </div>
          </div>

          <div className="bg-white p-8 rounded-2xl shadow-xl">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">Recent Donations</h2>
            
            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                {error}
                {!error.includes('login') && (
                  <button
                    onClick={fetchDonations}
                    className="mt-2 text-red-600 hover:text-red-800 font-semibold"
                  >
                    Try Again
                  </button>
                )}
              </div>
            )}

            {!error && donations.length === 0 ? (
              <p className="text-gray-600 text-center">No donations found</p>
            ) : (
              <div className="space-y-4">
                {donations.map((donation) => (
                  <div
                    key={donation._id}
                    className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow"
                  >
                    <div className="flex justify-between items-center">
                      <div>
                        <h3 className="font-semibold text-gray-800">{donation.charityName}</h3>
                        <p className="text-sm text-gray-600">
                          {new Date(donation.date).toLocaleDateString()}
                        </p>
                      </div>
                      <div className="text-lg font-bold text-purple-600">
                        ₹{donation.amount.toLocaleString()}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DonationHistory;
