import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UPIPayment from './UPIPayment';

const DonationForm = () => {
  const [formData, setFormData] = useState({
    amount: '',
    charityName: '',
    paymentMethod: 'card'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [totalDonations, setTotalDonations] = useState(0);

  useEffect(() => {
    const fetchTotalDonations = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        const response = await axios.get('http://localhost:5000/api/donations/total', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setTotalDonations(response.data.total);
      } catch (err) {
        console.error('Error fetching total donations:', err);
      }
    };

    fetchTotalDonations();
  }, []);

  const validateForm = () => {
    // Check if amount is a valid number
    const amountNum = parseFloat(formData.amount);
    if (isNaN(amountNum) || amountNum <= 0) {
      setError('Please enter a valid amount greater than 0');
      return false;
    }

    // Check if charityName is not empty
    if (!formData.charityName.trim()) {
      setError('Please enter a charity name');
      return false;
    }

    // Clear any existing errors if validation passes
    setError('');
    return true;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (error) setError('');
  };

  const submitDonation = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please login to make a donation');
        return;
      }

      await axios.post(
        'http://localhost:5000/api/donations',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setSuccess('Donation submitted successfully!');
      setFormData({ amount: '', charityName: '', paymentMethod: 'card' });
      
      // Update total donations
      const response = await axios.get('http://localhost:5000/api/donations/total', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setTotalDonations(response.data.total);
    } catch (err) {
      console.error('Error submitting donation:', err);
      if (err.response?.status === 401) {
        setError('Your session has expired. Please login again.');
        localStorage.removeItem('token');
      } else if (err.response?.status === 400) {
        setError(err.response.data.message || 'Invalid donation data');
      } else {
        setError('Failed to submit donation. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    if (!validateForm()) {
      setLoading(false);
      return;
    }

    await submitDonation();
  };

  const handleUPISuccess = () => {
    if (!validateForm()) {
      return;
    }
    setLoading(true);
    submitDonation();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Make a Difference</h1>
          <p className="text-xl text-gray-600">Your contribution helps us create a better world</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div className="bg-white p-8 rounded-2xl shadow-xl">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">Make a Donation</h2>
            
            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                {error}
              </div>
            )}
            
            {success && (
              <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
                {success}
              </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label htmlFor="amount" className="block text-sm font-medium text-gray-700 mb-1">
                  Amount (₹)
                </label>
                <input
                  type="number"
                  id="amount"
                  name="amount"
                  value={formData.amount}
                  onChange={handleChange}
                  required
                  min="1"
                  step="0.01"
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
                  placeholder="Enter amount"
                />
              </div>

              <div>
                <label htmlFor="charityName" className="block text-sm font-medium text-gray-700 mb-1">
                  Charity Name
                </label>
                <input
                  type="text"
                  id="charityName"
                  name="charityName"
                  value={formData.charityName}
                  onChange={handleChange}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
                  placeholder="Enter charity name"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-3">
                  Payment Method
                </label>
                <div className="space-y-2">
                  <label className="flex items-center space-x-2">
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="card"
                      checked={formData.paymentMethod === 'card'}
                      onChange={handleChange}
                      className="h-4 w-4 text-purple-600"
                    />
                    <span>Credit/Debit Card</span>
                  </label>
                  <label className="flex items-center space-x-2">
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="upi"
                      checked={formData.paymentMethod === 'upi'}
                      onChange={handleChange}
                      className="h-4 w-4 text-purple-600"
                    />
                    <span>UPI Payment</span>
                  </label>
                </div>
              </div>

              {formData.paymentMethod === 'upi' ? (
                <UPIPayment
                  amount={formData.amount}
                  onSuccess={handleUPISuccess}
                />
              ) : (
                <button
                  type="submit"
                  disabled={loading}
                  className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-3 px-6 rounded-lg transition duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? 'Processing...' : 'Donate Now'}
                </button>
              )}
            </form>
          </div>

          <div className="bg-white p-8 rounded-2xl shadow-xl">
            <div className="text-center">
              <h2 className="text-2xl font-bold text-gray-800 mb-4">Total Donations</h2>
              <div className="text-5xl font-bold text-purple-600 mb-4">
                ₹{totalDonations.toLocaleString()}
              </div>
              <p className="text-gray-600">Thank you for your generous contributions!</p>
            </div>
            
            <div className="mt-8">
              <h3 className="text-xl font-semibold text-gray-800 mb-4">How Your Donation Helps</h3>
              <ul className="space-y-3">
                <li className="flex items-center space-x-2">
                  <span className="text-purple-500">✓</span>
                  <span>Support essential services</span>
                </li>
                <li className="flex items-center space-x-2">
                  <span className="text-purple-500">✓</span>
                  <span>Fund community programs</span>
                </li>
                <li className="flex items-center space-x-2">
                  <span className="text-purple-500">✓</span>
                  <span>Make a lasting impact</span>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DonationForm;
