import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const DonationHistory = () => {
  const [donations, setDonations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [totalDonations, setTotalDonations] = useState(0);
  const navigate = useNavigate();

  const fetchDonations = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      // Fetch user's donations
      const donationsResponse = await axios.get('http://localhost:5000/api/donations/my-donations', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      // Fetch total donations
      const totalResponse = await axios.get('http://localhost:5000/api/donations/total', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      setDonations(donationsResponse.data);
      setTotalDonations(totalResponse.data.total || 0);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching donations:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setError('Failed to fetch donations. Please try again later.');
      }
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    fetchDonations();
  }, [fetchDonations]);

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-4xl mx-auto">
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-600"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-4xl mx-auto">
          <div className="bg-white rounded-2xl shadow-xl p-8 text-center">
            <div className="text-red-600 mb-4">{error}</div>
            <button
              onClick={fetchDonations}
              className="bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-2 px-6 rounded-lg transition duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
          <div className="p-8">
            <h2 className="text-3xl font-bold text-gray-900 mb-8">My Donations</h2>

            {/* Total Donations Card */}
            <div className="bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-xl p-6 mb-8 shadow-lg">
              <h3 className="text-xl font-semibold mb-2">Total Donations</h3>
              <p className="text-3xl font-bold">₹{totalDonations.toLocaleString()}</p>
            </div>

            {donations.length === 0 ? (
              <div className="text-center py-8">
                <p className="text-gray-600 text-lg">No donations found.</p>
                <Link
                  to="/donate"
                  className="inline-block mt-4 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-2 px-6 rounded-lg transition duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
                >
                  Make a Donation
                </Link>
              </div>
            ) : (
              <div className="space-y-6">
                {donations.map((donation) => (
                  <div
                    key={donation._id}
                    className="bg-gray-50 rounded-xl p-6 hover:shadow-lg transition duration-300"
                  >
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="text-xl font-semibold text-gray-900">{donation.charityName}</h3>
                        <p className="text-gray-600 mt-1">Donated on {formatDate(donation.createdAt)}</p>
                      </div>
                      <div className="text-right">
                        <p className="text-2xl font-bold text-purple-600">₹{donation.amount.toLocaleString()}</p>
                      </div>
                    </div>
                    <div className="mt-4">
                      <p className="text-gray-600">
                        <span className="font-semibold">Donor:</span> {donation.donorName}
                      </p>
                      <p className="text-gray-600 mt-1">
                        <span className="font-semibold">Address:</span> {donation.address}
                      </p>
                      <p className="text-gray-600 mt-1">
                        <span className="font-semibold">PIN Code:</span> {donation.pinCode}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DonationHistory;
