import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DonationHistory = () => {
  const navigate = useNavigate();
  const [donations, setDonations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [totalDonations, setTotalDonations] = useState(0);

  const fetchDonations = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      // Fetch user's donations
      const donationsResponse = await axios.get('http://localhost:5000/api/donations/my-donations', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      // Fetch total donations
      const totalResponse = await axios.get('http://localhost:5000/api/donations/total', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      setDonations(donationsResponse.data);
      setTotalDonations(totalResponse.data.total || 0);
    } catch (error) {
      console.error('Error fetching donations:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setError('Failed to fetch donations. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    fetchDonations();
  }, [fetchDonations]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 flex items-center justify-center">
        <div className="bg-white p-8 rounded-xl shadow-lg max-w-md w-full text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={fetchDonations}
            className="bg-purple-600 text-white px-6 py-2 rounded-lg hover:bg-purple-700 transition duration-300"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden mb-8">
          <div className="p-6 sm:p-8">
            <h2 className="text-3xl font-bold text-gray-900 mb-6">My Donations</h2>

            {/* Total Donations Card */}
            <div className="bg-gradient-to-r from-purple-600 to-pink-600 rounded-xl p-6 mb-8 text-white transform transition-all duration-300 hover:scale-105 hover:shadow-2xl">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-lg font-semibold mb-1">Total Donations</h3>
                  <p className="text-3xl font-bold">₹{totalDonations.toLocaleString()}</p>
                </div>
                <div className="bg-white/20 rounded-full p-4">
                  <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                </div>
              </div>
            </div>

            {/* Donations List */}
            <div className="space-y-6">
              {donations.length === 0 ? (
                <div className="text-center py-8">
                  <p className="text-gray-600">No donations found. Make your first donation today!</p>
                </div>
              ) : (
                donations.map((donation) => (
                  <div
                    key={donation._id}
                    className="bg-gray-50 rounded-xl p-6 hover:shadow-lg transition duration-300 transform hover:scale-[1.02]"
                  >
                    <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
                      <div className="mb-4 sm:mb-0">
                        <h3 className="text-xl font-semibold text-gray-900 mb-2">
                          {donation.charityName}
                        </h3>
                        <p className="text-gray-600 mb-2">
                          <span className="font-medium">Amount:</span> ₹{Number(donation.amount).toLocaleString()}
                        </p>
                        <p className="text-gray-600">
                          <span className="font-medium">Date:</span>{' '}
                          {new Date(donation.createdAt).toLocaleDateString('en-IN', {
                            year: 'numeric',
                            month: 'long',
                            day: 'numeric'
                          })}
                        </p>
                      </div>
                      <div className="flex items-center space-x-4">
                        <span className="text-2xl">{donation.categoryEmoji}</span>
                        <span className="text-2xl">{donation.paymentModeIcon}</span>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DonationHistory;
