import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DonationHistory = () => {
  const [donations, setDonations] = useState([]);
  const [totalDonations, setTotalDonations] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const fetchDonations = async () => {
    try {
      setLoading(true);
      setError('');
      const token = localStorage.getItem('token');

      if (!token) {
        setError('Please login to view your donation history');
        navigate('/login');
        return;
      }

      const [donationsResponse, totalResponse] = await Promise.all([
        axios.get('http://localhost:5000/api/donations/my-donations', {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get('http://localhost:5000/api/donations/total', {
          headers: { Authorization: `Bearer ${token}` }
        })
      ]);

      setDonations(donationsResponse.data);
      setTotalDonations(totalResponse.data.total || 0);
    } catch (err) {
      console.error('Error fetching donations:', err);
      if (err.response?.status === 401) {
        setError('Your session has expired. Please login again.');
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setError('Failed to fetch donations. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDonations();
  }, []);

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-4xl mx-auto">
          <div className="bg-white p-8 rounded-2xl shadow-xl text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading your donation history...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Your Donation History</h1>
          <p className="text-xl text-gray-600">Thank you for your generous contributions</p>
        </div>

        {error ? (
          <div className="bg-white p-8 rounded-2xl shadow-xl text-center">
            <div className="text-red-600 mb-4">{error}</div>
            <button
              onClick={fetchDonations}
              className="bg-purple-600 hover:bg-purple-700 text-white font-semibold py-2 px-4 rounded-lg transition duration-300"
            >
              Try Again
            </button>
          </div>
        ) : (
          <div className="space-y-8">
            {/* Total Donations Card */}
            <div className="bg-white p-8 rounded-2xl shadow-xl">
              <h2 className="text-2xl font-bold text-gray-800 mb-4">Total Contributions</h2>
              <div className="text-4xl font-bold text-purple-600 mb-2">₹{totalDonations.toLocaleString()}</div>
              <p className="text-gray-600">Thank you for making a difference!</p>
            </div>

            {/* Donation History */}
            <div className="bg-white p-8 rounded-2xl shadow-xl">
              <h2 className="text-2xl font-bold text-gray-800 mb-6">Recent Donations</h2>

              {donations.length === 0 ? (
                <div className="text-center py-8">
                  <p className="text-gray-600">No donations found. Start making a difference today!</p>
                </div>
              ) : (
                <div className="space-y-6">
                  {donations.map((donation) => (
                    <div key={donation._id} className="border-b border-gray-200 pb-6 last:border-0">
                      <div className="flex justify-between items-start">
                        <div>
                          <h3 className="text-lg font-semibold text-gray-800">{donation.charityName}</h3>
                          <p className="text-gray-600">Donated by {donation.donorName}</p>
                          <p className="text-sm text-gray-500">Date: {formatDate(donation.createdAt)}</p>
                        </div>
                        <div className="text-right">
                          <div className="text-xl font-bold text-purple-600">₹{donation.amount.toLocaleString()}</div>
                          <div className="text-sm text-gray-500 capitalize">{donation.paymentMethod}</div>
                        </div>
                      </div>
                      <div className="mt-2">
                        <p className="text-sm text-gray-600">
                          <span className="font-medium">Address:</span> {donation.address}, {donation.pinCode}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DonationHistory;
