import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DonationHistory = () => {
  const [donations, setDonations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const fetchDonations = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please login to view your donations');
        navigate('/login');
        return;
      }

      const response = await axios.get('http://localhost:5000/api/donations/my-donations', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDonations(response.data);
      setError('');
    } catch (err) {
      console.error('Error fetching donations:', err);
      if (err.response?.status === 401) {
        if (err.response?.data?.message === 'Token expired') {
          localStorage.removeItem('token');
          setError('Session expired. Please login again.');
          navigate('/login');
        } else {
          setError('Please login to view your donations');
          navigate('/login');
        }
      } else if (err.response?.status === 404) {
        setError('No donations found');
      } else {
        setError('Failed to load donations. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDonations();
    // Refresh donations every 30 seconds
    const interval = setInterval(fetchDonations, 30000);
    return () => clearInterval(interval);
  }, [navigate]);

  if (loading) {
    return (
      <div className="text-center py-4">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
        <p className="mt-2 text-gray-600">Loading donations...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-4">
        <div className="text-red-500 mb-2">{error}</div>
        <button
          onClick={fetchDonations}
          className="text-blue-600 hover:text-blue-800"
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <h2 className="text-xl font-bold">Your Donation History</h2>
      {donations.length === 0 ? (
        <p className="text-gray-500">No donations yet</p>
      ) : (
        <div className="space-y-2">
          {donations.map((donation) => (
            <div
              key={donation._id}
              className="bg-white p-4 rounded-lg shadow"
            >
              <div className="flex justify-between items-center">
                <div>
                  <p className="text-sm text-gray-500">
                    {new Date(donation.date).toLocaleDateString()}
                  </p>
                  <p className="text-sm text-gray-600">{donation.charityName}</p>
                </div>
                <p className="font-bold">${donation.amount.toFixed(2)}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default DonationHistory;
