import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import ThankYouMessage from './ThankYouMessage';

const donationCategories = [
  { id: 'food', name: 'Food Donation', emoji: 'ðŸ±' },
  { id: 'clothing', name: 'Clothing Donation', emoji: 'ðŸ‘•' },
  { id: 'cancer', name: 'Cancer Patient Support', emoji: 'ðŸŽ—ï¸' },
  { id: 'shelter', name: 'Shelter Support', emoji: 'ðŸ ' },
  { id: 'education', name: 'Education Aid', emoji: 'ðŸ“š' },
  { id: 'medical', name: 'Medical Supplies', emoji: 'ðŸ’Š' },
  { id: 'hygiene', name: 'Hygiene Kits', emoji: 'ðŸ§¼' },
  { id: 'festival', name: 'Festival Hampers', emoji: 'ðŸŽ' },
  { id: 'technology', name: 'Technology for Students', emoji: 'ðŸ’»' },
  { id: 'childcare', name: 'Child Care Essentials', emoji: 'ðŸ¼' }
];

const paymentModes = [
  { id: 'upi', name: 'UPI', icon: 'ðŸ’¸' },
  { id: 'card', name: 'Credit/Debit Card', icon: 'ðŸ’³' },
  { id: 'netbanking', name: 'Net Banking', icon: 'ðŸ¦' },
  { id: 'wallet', name: 'Digital Wallet', icon: 'ðŸ“±' }
];

const DonationForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    amount: '',
    charityName: '',
    category: '',
    donorName: '',
    address: '',
    pinCode: '',
    paymentMode: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [showThankYou, setShowThankYou] = useState(false);
  const [donationDetails, setDonationDetails] = useState({
    amount: '',
    charityName: '',
    category: '',
    paymentMode: ''
  });
  const [totalDonations, setTotalDonations] = useState(0);

  useEffect(() => {
    const fetchTotalDonations = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await axios.get('http://localhost:5000/api/donations/total', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (response.status === 200) {
          setTotalDonations(response.data.total);
        }
      } catch (error) {
        console.error('Error fetching total donations:', error);
      }
    };

    fetchTotalDonations();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors({ ...errors, [name]: '' });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.amount || isNaN(formData.amount) || formData.amount <= 0) {
      newErrors.amount = 'Please enter a valid amount';
    }
    if (!formData.charityName.trim()) {
      newErrors.charityName = 'Charity name is required';
    }
    if (!formData.category) {
      newErrors.category = 'Please select a donation category';
    }
    if (!formData.donorName.trim()) {
      newErrors.donorName = 'Donor name is required';
    }
    if (!formData.address.trim()) {
      newErrors.address = 'Address is required';
    }
    if (!formData.pinCode.match(/^\d{6}$/)) {
      newErrors.pinCode = 'PIN code must be 6 digits';
    }
    if (!formData.paymentMode) {
      newErrors.paymentMode = 'Please select a payment mode';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const response = await axios.post(
        'http://localhost:5000/api/donations',
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        }
      );

      if (response.status === 201) {
        // Store donation details before resetting form
        setDonationDetails({
          amount: formData.amount,
          charityName: formData.charityName,
          category: formData.category,
          paymentMode: formData.paymentMode
        });

        // Reset form
        setFormData({
          amount: '',
          charityName: '',
          category: '',
          donorName: '',
          address: '',
          pinCode: '',
          paymentMode: ''
        });

        // Show thank you message
        setShowThankYou(true);
      }
    } catch (error) {
      console.error('Error creating donation:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setErrors({ submit: 'Failed to create donation. Please try again.' });
      }
    } finally {
      setLoading(false);
    }
  };

  if (showThankYou) {
    return (
      <ThankYouMessage
        donationAmount={donationDetails.amount}
        charityName={donationDetails.charityName}
        category={donationDetails.category}
        paymentMode={donationDetails.paymentMode}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md mx-auto">
        {/* Total Donations Section */}
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden mb-8 transform transition-all duration-300 hover:scale-105">
          <div className="px-6 py-8">
            <div className="text-center">
              <h3 className="text-2xl font-bold text-gray-900 mb-2">Total Donations</h3>
              <div className="text-4xl font-bold text-purple-600 mb-4">
                â‚¹{totalDonations.toLocaleString()}
              </div>
              <p className="text-gray-600">
                Together, we've made a difference in countless lives
              </p>
            </div>
          </div>
        </div>

        {/* Donation Form */}
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
          <div className="px-6 py-8">
            <h2 className="text-3xl font-bold text-center text-gray-900 mb-8">Make a Donation</h2>

            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Amount Field */}
              <div>
                <label htmlFor="amount" className="block text-sm font-medium text-gray-700 mb-1">
                  Amount (â‚¹)
                </label>
                <input
                  type="number"
                  id="amount"
                  name="amount"
                  value={formData.amount}
                  onChange={handleChange}
                  className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-600 focus:border-transparent ${
                    errors.amount ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="Enter amount"
                />
                {errors.amount && <p className="mt-1 text-sm text-red-600">{errors.amount}</p>}
              </div>

              {/* Charity Name Field */}
              <div>
                <label htmlFor="charityName" className="block text-sm font-medium text-gray-700 mb-1">
                  Charity Name
                </label>
                <input
                  type="text"
                  id="charityName"
                  name="charityName"
                  value={formData.charityName}
                  onChange={handleChange}
                  className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-600 focus:border-transparent ${
                    errors.charityName ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="Enter charity name"
                />
                {errors.charityName && <p className="mt-1 text-sm text-red-600">{errors.charityName}</p>}
              </div>

              {/* Category Field */}
              <div>
                <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">
                  Donation Category
                </label>
                <select
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-600 focus:border-transparent ${
                    errors.category ? 'border-red-500' : 'border-gray-300'
                  }`}
                >
                  <option value="">Select a category</option>
                  {donationCategories.map((category) => (
                    <option key={category.id} value={category.id}>
                      {category.emoji} {category.name}
                    </option>
                  ))}
                </select>
                {errors.category && <p className="mt-1 text-sm text-red-600">{errors.category}</p>}
              </div>

              {/* Donor Name Field */}
              <div>
                <label htmlFor="donorName" className="block text-sm font-medium text-gray-700 mb-1">
                  Your Name
                </label>
                <input
                  type="text"
                  id="donorName"
                  name="donorName"
                  value={formData.donorName}
                  onChange={handleChange}
                  className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-600 focus:border-transparent ${
                    errors.donorName ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="Enter your name"
                />
                {errors.donorName && <p className="mt-1 text-sm text-red-600">{errors.donorName}</p>}
              </div>

              {/* Address Field */}
              <div>
                <label htmlFor="address" className="block text-sm font-medium text-gray-700 mb-1">
                  Address
                </label>
                <textarea
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  rows="3"
                  className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-600 focus:border-transparent ${
                    errors.address ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="Enter your address"
                />
                {errors.address && <p className="mt-1 text-sm text-red-600">{errors.address}</p>}
              </div>

              {/* PIN Code Field */}
              <div>
                <label htmlFor="pinCode" className="block text-sm font-medium text-gray-700 mb-1">
                  PIN Code
                </label>
                <input
                  type="text"
                  id="pinCode"
                  name="pinCode"
                  value={formData.pinCode}
                  onChange={handleChange}
                  className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-purple-600 focus:border-transparent ${
                    errors.pinCode ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="Enter 6-digit PIN code"
                  maxLength="6"
                />
                {errors.pinCode && <p className="mt-1 text-sm text-red-600">{errors.pinCode}</p>}
              </div>

              {/* Payment Mode Selection */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Select Payment Mode
                </label>
                <div className="grid grid-cols-2 gap-4">
                  {paymentModes.map((mode) => (
                    <button
                      key={mode.id}
                      type="button"
                      onClick={() => handleChange({ target: { name: 'paymentMode', value: mode.id } })}
                      className={`p-4 border rounded-lg text-center transition-all duration-300 transform hover:scale-105 ${
                        formData.paymentMode === mode.id
                          ? 'border-purple-600 bg-purple-50'
                          : 'border-gray-300 hover:border-purple-400'
                      }`}
                    >
                      <span className="text-2xl block mb-2">{mode.icon}</span>
                      <span className="text-sm font-medium">{mode.name}</span>
                    </button>
                  ))}
                </div>
                {errors.paymentMode && (
                  <p className="mt-1 text-sm text-red-600">{errors.paymentMode}</p>
                )}
              </div>

              {errors.submit && (
                <div className="text-red-600 text-sm text-center">{errors.submit}</div>
              )}

              <button
                type="submit"
                disabled={loading}
                className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-3 px-4 rounded-lg transition duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? 'Processing...' : 'Proceed to Payment'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DonationForm;
