import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UserProfile = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    totalDonations: 0,
    lastDonation: null,
    favoriteCharity: null
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        console.log('Token:', token); // Debug log

        if (!token) {
          console.log('No token found, redirecting to login'); // Debug log
          navigate('/login');
          return;
        }

        console.log('Fetching user data...'); // Debug log
        const response = await axios.get('http://localhost:5000/api/users/profile', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        console.log('Response:', response.data); // Debug log

        if (response.data) {
          setUserData({
            name: response.data.name || 'User',
            email: response.data.email || '',
            totalDonations: response.data.totalDonations || 0,
            lastDonation: response.data.lastDonation || null,
            favoriteCharity: response.data.favoriteCharity || 'No favorite charity yet'
          });
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
        if (error.response?.status === 401) {
          console.log('Unauthorized, removing token and redirecting'); // Debug log
          localStorage.removeItem('token');
          navigate('/login');
        } else {
          setError('Failed to load profile data. Please try again later.');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={() => navigate('/')}
            className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
          >
            Return to Home
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
          {/* Profile Header */}
          <div className="bg-gradient-to-r from-purple-600 to-pink-600 p-8 text-white">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold">{userData.name}</h1>
                <p className="text-purple-100">{userData.email}</p>
              </div>
              <button
                onClick={handleLogout}
                className="bg-white text-purple-600 px-4 py-2 rounded-lg hover:bg-purple-50 transition-colors"
              >
                Logout
              </button>
            </div>
          </div>

          {/* Stats Section */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 p-8">
            <div className="bg-purple-50 rounded-xl p-6 text-center">
              <div className="text-4xl font-bold text-purple-600 mb-2">₹{userData.totalDonations}</div>
              <div className="text-gray-600">Total Donations</div>
            </div>
            <div className="bg-pink-50 rounded-xl p-6 text-center">
              <div className="text-4xl font-bold text-pink-600 mb-2">
                {userData.lastDonation ? new Date(userData.lastDonation).toLocaleDateString() : 'N/A'}
              </div>
              <div className="text-gray-600">Last Donation</div>
            </div>
            <div className="bg-purple-50 rounded-xl p-6 text-center">
              <div className="text-4xl font-bold text-purple-600 mb-2">
                {userData.favoriteCharity}
              </div>
              <div className="text-gray-600">Favorite Charity</div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="p-8 border-t border-gray-200">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Quick Actions</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <button
                onClick={() => navigate('/donate')}
                className="bg-gradient-to-r from-purple-600 to-pink-600 text-white p-6 rounded-xl hover:from-purple-700 hover:to-pink-700 transition-all duration-300 transform hover:scale-105"
              >
                <div className="text-2xl mb-2">💝</div>
                <div className="font-semibold">Make a Donation</div>
              </button>
              <button
                onClick={() => navigate('/my-donations')}
                className="bg-gradient-to-r from-purple-600 to-pink-600 text-white p-6 rounded-xl hover:from-purple-700 hover:to-pink-700 transition-all duration-300 transform hover:scale-105"
              >
                <div className="text-2xl mb-2">📋</div>
                <div className="font-semibold">View Donation History</div>
              </button>
              <button
                onClick={() => navigate('/charities')}
                className="bg-gradient-to-r from-purple-600 to-pink-600 text-white p-6 rounded-xl hover:from-purple-700 hover:to-pink-700 transition-all duration-300 transform hover:scale-105"
              >
                <div className="text-2xl mb-2">🏛️</div>
                <div className="font-semibold">Browse Charities</div>
              </button>
            </div>
          </div>

          {/* Recent Activity */}
          <div className="p-8 border-t border-gray-200">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Recent Activity</h2>
            <div className="space-y-4">
              <div className="bg-gray-50 p-4 rounded-lg">
                <p className="text-gray-600">No recent activity to show</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
