import React, { useState } from 'react';
import axios from 'axios';
import UPIPayment from './UPIPayment';

const DonationForm = () => {
  const [formData, setFormData] = useState({
    amount: '',
    description: '',
    paymentMethod: 'card'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showUPI, setShowUPI] = useState(false);

  const validateForm = () => {
    // Check if amount is a valid number
    const amountNum = parseFloat(formData.amount);
    if (isNaN(amountNum) || amountNum <= 0) {
      setError('Please enter a valid amount greater than 0');
      return false;
    }

    // Check if description is not empty
    if (!formData.description.trim()) {
      setError('Please enter a charity name');
      return false;
    }

    // Clear any existing errors if validation passes
    setError('');
    return true;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (error) setError('');
  };

  const submitDonation = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please login to make a donation');
        return;
      }

      await axios.post(
        'http://localhost:5000/api/donations',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setSuccess('Donation submitted successfully!');
      setFormData({ amount: '', description: '', paymentMethod: 'card' });
      setShowUPI(false);
    } catch (err) {
      console.error('Error submitting donation:', err);
      if (err.response?.status === 401) {
        setError('Your session has expired. Please login again.');
        localStorage.removeItem('token');
      } else if (err.response?.status === 400) {
        setError(err.response.data.message || 'Invalid donation data');
      } else {
        setError('Failed to submit donation. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    if (!validateForm()) {
      setLoading(false);
      return;
    }

    await submitDonation();
  };

  const handleUPISuccess = () => {
    if (!validateForm()) {
      return;
    }
    setLoading(true);
    submitDonation();
  };

  return (
    <div className="max-w-md mx-auto bg-white p-8 rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-gray-800 mb-6">Make a Donation</h2>
      
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}
      
      {success && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
          {success}
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label htmlFor="amount" className="block text-sm font-medium text-gray-700 mb-1">
            Amount (â‚¹)
          </label>
          <input
            type="number"
            id="amount"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            required
            min="1"
            step="0.01"
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            placeholder="Enter amount"
          />
        </div>

        <div>
          <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
            Charity Name
          </label>
          <input
            type="text"
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            placeholder="Enter charity name"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-3">
            Payment Method
          </label>
          <div className="space-y-2">
            <label className="flex items-center space-x-2">
              <input
                type="radio"
                name="paymentMethod"
                value="card"
                checked={formData.paymentMethod === 'card'}
                onChange={handleChange}
                className="h-4 w-4 text-blue-600"
              />
              <span>Credit/Debit Card</span>
            </label>
            <label className="flex items-center space-x-2">
              <input
                type="radio"
                name="paymentMethod"
                value="upi"
                checked={formData.paymentMethod === 'upi'}
                onChange={(e) => {
                  handleChange(e);
                  setShowUPI(true);
                }}
                className="h-4 w-4 text-blue-600"
              />
              <span>UPI Payment</span>
            </label>
          </div>
        </div>

        {formData.paymentMethod === 'upi' && showUPI && (
          <UPIPayment
            amount={formData.amount}
            onSuccess={handleUPISuccess}
          />
        )}

        {formData.paymentMethod === 'card' && (
          <button
            type="submit"
            disabled={loading}
            className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-3 px-6 rounded-lg transition duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Processing...' : 'Donate Now'}
          </button>
        )}
      </form>
    </div>
  );
};

export default DonationForm;
